import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Route, Routes, Link, useNavigate } from "react-router-dom";
import "./styles.css";

const Gallery = ({ selectedImages, setSelectedImages }) => {
 const [images, setImages] = useState([]);

useEffect(() => {
  fetch("http://localhost:3001/api/images")
    .then(response => response.json())
    .then(data => setImages(data))
    .catch(error => console.error("Greška pri učitavanju slika:", error));
}, []);


  // Pozivamo fetchImages svakih 5 minuta
  useEffect(() => {
    fetchImages(); // Na početku pozovemo odmah
    const intervalId = setInterval(fetchImages, 5 * 60 * 1000); // Ažuriranje svakih 5 minuta

    return () => clearInterval(intervalId); // Čistimo interval kada komponenta bude unmounted
  }, []);

  const toggleSelection = (image) => {
    setSelectedImages((prev) =>
      prev.includes(image) ? prev.filter((img) => img !== image) : [...prev, image]
    );
  };

  return (
    <div className="container">
      <h1>Odaberite fotografije</h1>
      <div className="grid">
        {images.map((image) => (
          <div key={image} className="border" onClick={() => toggleSelection(image)}>
            <img
              src={`http://localhost:3001/images/${image}`} // Putanja do slike sa servera
              alt={image}
              className={selectedImages.includes(image) ? "selected" : ""}
            />
          </div>
        ))}
      </div>
      <Link to="/form" className="mt-4">Dalje</Link>
    </div>
  );
};

const Form = ({ name, setName }) => {
  const navigate = useNavigate();
  return (
    <div className="container">
      <h1>Unesite ime i prezime</h1>
      <input
        type="text"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="border"
        placeholder="Ime i prezime"
      />
      <button onClick={() => navigate("/checkout")} className="mt-4">
        Dalje
      </button>
    </div>
  );
};

const Checkout = ({ selectedImages, name }) => {
  const total = selectedImages.length * 2;
  return (
    <div className="container">
      <h1>Korpa</h1>
      <p>Ime: {name}</p>
      <p>Broj slika: {selectedImages.length}</p>
      <p>Ukupna cena: {total} EUR</p>
      <div className="checkout-info">
        <Link to="/confirmation" className="mt-4">Plati gotovinom</Link>
        <button className="bg-yellow-500">Plati PayPal-om</button>
      </div>
    </div>
  );
};

const Confirmation = () => (
  <div className="container">
    <h1>Porudžbina prosleđena!</h1>
    <p>Dođite do pulta za 30 minuta.</p>
  </div>
);

const App = () => {
  const [selectedImages, setSelectedImages] = useState([]);
  const [name, setName] = useState("");

  return (
    <Router>
      <Routes>
        <Route path="/" element={<Gallery selectedImages={selectedImages} setSelectedImages={setSelectedImages} />} />
        <Route path="/form" element={<Form name={name} setName={setName} />} />
        <Route path="/checkout" element={<Checkout selectedImages={selectedImages} name={name} />} />
        <Route path="/confirmation" element={<Confirmation />} />
      </Routes>
    </Router>
  );
};

export default App;
