import { useState } from "react";
import Zoom from "react-medium-image-zoom";
import "react-medium-image-zoom/dist/styles.css";

const ImageSelector = ({ src, alt, onSelect, isSelected, onClick }) => {
  return (
    <div className="relative w-48 h-48 border rounded-lg overflow-hidden">
      {/* Zumiranje slike */}
      <Zoom>
        <img
          src={src}
          alt={alt}
          className="w-full h-full object-cover cursor-pointer transition-transform duration-300 hover:scale-110"
          onClick={onClick}
        />
      </Zoom>

      {/* Checkbox kao overlay */}
      <input
        type="checkbox"
        checked={isSelected}
        onChange={onSelect}
        className="absolute top-2 left-2 w-6 h-6 cursor-pointer"
      />
    </div>
  );
};

const ImageGallery = () => {
  const images = [
    { id: 1, src: "https://via.placeholder.com/300", alt: "Slika 1" },
    { id: 2, src: "https://via.placeholder.com/300", alt: "Slika 2" },
    { id: 3, src: "https://via.placeholder.com/300", alt: "Slika 3" },
  ];

  const [selectedImages, setSelectedImages] = useState([]);

  const toggleSelection = (id) => {
    setSelectedImages((prev) =>
      prev.includes(id) ? prev.filter((imageId) => imageId !== id) : [...prev, id]
    );
  };

  return (
    <div className="grid grid-cols-3 gap-4 p-4">
      {images.map((image) => (
        <ImageSelector
          key={image.id}
          src={image.src}
          alt={image.alt}
          isSelected={selectedImages.includes(image.id)}
          onSelect={() => toggleSelection(image.id)}
        />
      ))}

      {/* Dugme za prikaz izabranih slika */}
      <button
        onClick={() => console.log(selectedImages)}
        className="mt-4 p-2 bg-blue-500 text-white rounded w-full"
      >
        Dodaj u korpu
      </button>
    </div>
  );
};

export default ImageGallery;
